mapper.insert:
insert into MOV_MOVIE ( movie_cd, movie_nm, movie_nm_en, prdt_year, open_dt, type_nm, prdt_stat_nm, nation_alt, genre_alt, rep_genre_nm, people_nm
) VALUES ( #{migMovieBean.movieCd}, #{migMovieBean.movieNm}, #{migMovieBean.movieNmEn}, #{migMovieBean.prdtYear}, DATE_FORMAT(#{migMovieBean.openDt}, '%Y-%m-%d %h:%m:%s'), #{migMovieBean.typeNm}, #{migMovieBean.prdtStatNm}, #{migMovieBean.nationAlt}, #{migMovieBean.genreAlt}, #{migMovieBean.repGenreNm}, #{migMovieBean.peopleNm}
)

insert into MOV_COMPANY_MAP
(
 MOVIE_CD,
 COMPANY_CD
 )
VALUES (
           #{migMovieBean.movieCd},
           #{migMovieBean.companyCd}
        )

insert into MOV_NATION_MAP
(
 NATION_CD,
 MOVIE_CD
 )
values (
        #{migMovieNationBean.nationCd},
        #{migMovieNationBean.movieCd}
        )

mapper.select:
select count(COMPANY_CD)
from MOV_COMPANY
where company_cd =  #{migMovieBean.companyCd}

mapper.insert:
insert into MOV_GENRE
    (
        genre_cd,
        genre_nm
    )
    VALUES (
           #{migMovieGenreBean.genreCd},
           #{migMovieGenreBean.genreNm}
           )

insert into MOV_GENRE_MAP
    (
        genre_cd,
        movie_cd
    )
    VALUES (
           #{migMovieGenreBean.genreCd},
           #{migMovieGenreBean.movieCd}
           )

mapper.select:
select count(*) cnt
from MOV_PEOPLE_INFO
where people_cd = #{peopleCd}
