mapper.update:
UPDATE MOV_MOVIE
SET STATE = #{mode}
WHERE MOVIE_CD IN

    #{movie.movieCd}

mapper.select:
SELECT mm.movie_cd, mm.movie_nm, mm.movie_nm_en, mm.prdt_year, mm.open_dt, mm.type_nm, mm.prdt_stat_nm, mm.genre_alt, mm.rep_genre_nm, mm.STATE, mmi.movie_nm_og, mmi.show_tm, mmi.nation_nm, mmi.cast, mmi.cast_en, mmi.show_types, mmi.show_type_group_nm, mmi.show_type_nm, mmi.audit_no, mmi.watch_grade_nm, mmi.point_yn, mmi.point_avg, mmi.point_total_cnt, mmi.total_view_cnt, mmi.preview_url, mmi.plot,
cf.FILE_NAME, cf.LOCAL_NAME, cf.FILE_EXT, cf.URL, cf.VOLUME, cf.CREATE_DT, cf.SRC
FROM MOV_MOVIE AS mm JOIN MOV_MOVIE_INFO AS mmi ON mm.MOVIE_CD = mmi.MOVIE_CD LEFT JOIN MOV_FILE_MAPPING AS mfm ON mm.MOVIE_CD = mfm.MOVIE_CD LEFT JOIN CMM_FILE AS cf ON mfm.FILE_ID = cf.FILE_ID and mfm.state = 'B' /* 파일매핑 1개로 제한 */ WHERE mm.movie_cd = #{movieBean.movieCd}

SELECT count(*) cnt
FROM MOV_FAVORITE
WHERE MOVIE_CD = #{movieBean.movieCd} AND MEM_ID = #{memberBean.memId}

mapper.update:
UPDATE MOV_PERSONAL_MOVIE_POINT
SET state = #{mode}
WHERE movie_cd = #{moviePersonalMoviePointBean.movieCd} AND mem_id = #{moviePersonalMoviePointBean.memId}

UPDATE MOV_FILE_MAPPING
SET STATE = 'D'
WHERE MOVIE_CD = #{movieBean.movieCd}
